import React, {useState} from 'react';
import {CommandType} from '../../../commands/commandTypes';
import {Command} from '../../../commands/commandTypes';
import styles from './AddCommand.module.css';
import TypeSpecificFields from "../TypeSpecificFields/TypeSpecificFields";
import {saveCommands} from "../../../dataAccess";


const AddCommand = () => {
    const [newCommand, setNewCommand] = useState({
        name: "",
        shortcut: "",
        type: CommandType.Navigate,
        fields: {},
    });

    const handleSelectChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
      const { name, value } = e.target;
      setNewCommand((prevState) => ({
        ...prevState,
        [name]: value,
      }));
    };

    const handleFieldChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const {name, value} = e.target;
        setNewCommand({
            ...newCommand,
            fields: {
                ...newCommand.fields,
                [name]: value,
            },
        });
    };

    const handleSubmit = async () => {
        console.log('Saving command:', newCommand);
        try {
            await saveCommands([newCommand]);
            console.log('New command saved:', newCommand);
        } catch (error) {
            console.error('Error saving command:', error);
        }
    };


    const commandValues = Object.values(CommandType);

    return (
        <div className={styles.container}>
            <h2 className={styles.title}>Add Command</h2>
            <label className={styles.label}>
                Name:
                <input
                    className={styles.input}
                    type="text"
                    name="name"
                    value={newCommand.name}
                    onChange={(e) => setNewCommand((prevState) => ({...prevState, [e.target.name]: e.target.value,}))}
                />
            </label>
            <label className={styles.label}>
                Shortcut:
                <input
                    type="text"
                    className={styles.input}
                    name="shortcut"
                    value={newCommand.shortcut}
                   onChange={(e) => setNewCommand((prevState) => ({...prevState, [e.target.name]: e.target.value,}))}
                />
            </label>
            <label className={styles.label}>
                Type:
                <select
                    name="type"
                    className={styles.commandType}
                    value={newCommand.type}
                    onChange={handleSelectChange}
                >
                    {commandValues.map((value) => (
                        <option key={value} value={value}>
                            {value}
                        </option>
                    ))}
                </select>
            </label>
            <TypeSpecificFields
                type={newCommand.type}
                fields={newCommand.fields}
                handleFieldChange={handleFieldChange}
            />
            <button className={styles.saveButton} onClick={handleSubmit}>Add Command</button>
        </div>
    );
};

export default AddCommand;
